$(document).ready(function(){var navbar=$(".navbar-default");var btnToggle=$(".navbar-toggle");var header=$(".navbar-scrolling");var didScroll=false;var changeHeaderOn=200;$("body").scrollspy({target:".navbar-fixed-top",offset:80});$("a.page-scroll").bind("click",function(event){var link=$(this);$("html, body").stop().animate({scrollTop:$(link.attr("href")).offset().top-70},500);if(btnToggle.is(":visible")){btnToggle.click()}event.preventDefault()});if(navbar.hasClass("navbar-scrolling")){btnToggle.bind("click",function(event){if($(event.target).attr("aria-expanded")==="false"){navbar.addClass("navbar-scroll")}else{if(window.pageYOffset<200){navbar.removeClass("navbar-scroll")}}})}window.addEventListener("scroll",function(){if(!didScroll){didScroll=true;setTimeout(scrollPage,250)}},false);function scrollPage(){if(header!==undefined){if(btnToggle.attr("aria-expanded")==="false"){var sy=scrollY();if(sy>=changeHeaderOn){header.addClass("navbar-scroll")}else{header.removeClass("navbar-scroll")}}didScroll=false}}function scrollY(){return window.pageYOffset||document.documentElement.scrollTop}});(new WOW).init();